import React from 'react'
import styled from 'styled-components'
import ColourPicker from '../common/ColourPicker';
import { Flex } from '../../styling/styled'

const ConfigurationWrapper = styled.div`
    background: ${(props) => props.backgroundColor};
    border-radius: 20px;
    box-shadow: ${(props) => ` ${props.distance}px ${props.distance}px 56px #56958d, ${props.distance}px ${props.distance}px 56px #86e9dd`};
    min-height: 450px;
    min-width: 400px;
    padding: 30px;
`;

export default ({
    backgroundColor,
    setBackgroundColor,
    size, 
    setSize,
    borderRadius,
    setBorderRadius,
    distance,
    setDistance
}) => {
    return (
        <ConfigurationWrapper backgroundColor={backgroundColor} distance={distance}>
            <div>
                <ColourPicker 
                    backgroundColor={backgroundColor}
                    setBackgroundColor={setBackgroundColor}
                />
            </div>

            <Flex>
                <div>Size: </div>
                <input 
                    type="range" 
                    min="50" 
                    max="500" 
                    value={size}
                    onChange={(e) => setSize(e.target.value)}
                    />
            </Flex>

            <Flex>
                <div>Border Radius: </div>
                <input 
                    type="range" 
                    min="10" 
                    max="200" 
                    value={borderRadius}
                    onChange={(e) => setBorderRadius(e.target.value)}
                    />
            </Flex>

            <Flex>
                <div>Distance: </div>
                <input 
                    type="range" 
                    min="5" 
                    max="50" 
                    value={distance}
                    onChange={(e) => setDistance(e.target.value)}
                    />
            </Flex>

        </ConfigurationWrapper>
    )
}
